CREATE USER 'clientsdb'@'localhost' IDENTIFIED WITH mysql_native_password BY '***';GRANT SELECT, INSERT, UPDATE, DELETE, CREATE, DROP, FILE, INDEX, ALTER, CREATE TEMPORARY TABLES, CREATE VIEW, EVENT, TRIGGER, SHOW VIEW, CREATE ROUTINE, ALTER ROUTINE, EXECUTE ON *.* TO 'clientsdb'@'localhost';ALTER USER 'clientsdb'@'localhost' REQUIRE NONE WITH MAX_QUERIES_PER_HOUR 0 MAX_CONNECTIONS_PER_HOUR 0 MAX_UPDATES_PER_HOUR 0 MAX_USER_CONNECTIONS 0;CREATE DATABASE IF NOT EXISTS `clientsdb`;GRANT ALL PRIVILEGES ON `clientsdb`.* TO 'clientsdb'@'localhost';
